package com.sist.exam03;

public class InitialzeAndNotInitialize {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 0;  //0이라는 값으로 초기화
		int b;		//변수만 선언하고 아무값도 담기지 않음. 이상태로는 사용할 수 없음
		
		String c = null; // null이라고초기화
		String d; //변수만 선언하고 아무값도 담기지 않음; 이상태로는 사용할 수 없다.
		
		
	}

}
